/** *dianping.com Inc *Copyright(c)2004-2016 All Rights Reserved. */package com.dianping.event.entity;import java.io.Serializable;import java.util.Arrays;import java.util.Date;import java.util.List;/** * @author zhangjie * @version $Id: ReportAggregationStateTracker.java, v 0.1 2016-04-11 下午3:50 zhangjie Exp $$ */public class ReportAggregationStateTracker implements Serializable {    private int shopAccountId;    private boolean hasTimeOut;    private boolean isComplete;    private List<DailyReportInfo> dailyReportInfoList;    private Date startTime;    public ReportAggregationStateTracker append(DailyReportInfo dailyReportInfo) {        if (dailyReportInfoList != null) {            dailyReportInfoList.add(dailyReportInfo);            return this;        } else {            ReportAggregationStateTracker tracker = new ReportAggregationStateTracker();            tracker.setDailyReportInfoList(Arrays.asList(dailyReportInfo));            return tracker;        }    }    public boolean isHasTimeOut() {        return hasTimeOut;    }    public void setHasTimeOut(boolean hasTimeOut) {        this.hasTimeOut = hasTimeOut;    }    public boolean isComplete() {        return isComplete;    }    public void setComplete(boolean isComplete) {        this.isComplete = isComplete;    }    public List<DailyReportInfo> getDailyReportInfoList() {        return dailyReportInfoList;    }    public void setDailyReportInfoList(List<DailyReportInfo> dailyReportInfoList) {        this.dailyReportInfoList = dailyReportInfoList;    }    public int getShopAccountId() {        return shopAccountId;    }    public void setShopAccountId(int shopAccountId) {        this.shopAccountId = shopAccountId;    }    public Date getStartTime() {        return startTime;    }    public void setStartTime(Date startTime) {        this.startTime = startTime;    }}